import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import alcImage from "../assets/alc.jpeg";
import "./UserCard.css";

export const UserCard = () => {
  const [isOpen, setIsOpen] = useState(false);
  const contentRef = useRef(null);
  const navigate = useNavigate();

  // Hardcoded Product List
  const [products, setProducts] = useState([
    { id: "vodka", name: "Magic Moments Grain Vodka", img: alcImage, description: "Smooth and refined vodka." },
    { id: "whiskey", name: "Magic Moments Grain Whiskey", img: alcImage, description: "Aged whiskey with a rich taste." },
  ]);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <div>
      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "open" : ""}`}>
        <button className="menu-btn inside-btn" onClick={toggleMenu}>←</button>
        <button onClick={() => navigate("/")}>Home</button>
        <button onClick={() => navigate("/about")}>About</button>
        <button onClick={() => navigate("/services")}>Services</button>
        <button onClick={() => navigate("/contact")}>Contact</button>
      </div>

      {/* Main menu button */}
      {!isOpen && <button className="menu-btn" onClick={toggleMenu}>☰ Menu</button>}

      {/* Content Section */}
      <div className="content" ref={contentRef}>
        <div className="container">
          {products.map((product) => (
            <div key={product.id} className="box" onClick={() => navigate(`/product/${product.id}`)}>
              <img src={product.img} alt={product.name} className="product-img" />
              <p className="description"><strong>{product.name}</strong></p>
              <p>{product.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default UserCard;
