import React, { useState } from "react";

const AddProduct = ({ addProduct }) => { // Accept addProduct as prop
  const [product, setProduct] = useState({ name: "", description: "", img: null });

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setProduct({ ...product, img: URL.createObjectURL(file) });
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    
    if (!product.name.trim() || !product.description.trim() || !product.img) {
      alert("Please fill in all fields including an image.");
      return;
    }

    addProduct(product); // ✅ Call addProduct from props
    setProduct({ name: "", description: "", img: null }); // Reset form
  };

  return (
    <div>
      <h2>Add New Product</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Product Name"
          value={product.name}
          onChange={(e) => setProduct({ ...product, name: e.target.value })}
        />
        <textarea
          placeholder="Product Description"
          value={product.description}
          onChange={(e) => setProduct({ ...product, description: e.target.value })}
        />
        <input type="file" onChange={handleImageUpload} />
        <button type="submit">Add Product</button>
      </form>
    </div>
  );
};

export default AddProduct;
