import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; 
import "./UserCard.css";
import placeholderImg from "../assets/placeholder.jpg"; // Import default placeholder image

const UserCard = ({ products, setProducts }) => { 
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  // Toggle Sidebar
  const toggleMenu = () => setIsOpen(!isOpen);

  // Function to Delete the First Product
  const deleteProduct = () => {
    if (products.length === 0) {
      alert("No products to delete!");
      return;
    }
    
    const updatedProducts = products.slice(1);
    setProducts(updatedProducts);
    localStorage.setItem("products", JSON.stringify(updatedProducts)); // Update local storage
  };

  // Get image path properly
  const getImagePath = (img) => {
    try {
      return require(`../assets/${img}`);
    } catch (error) {
      return placeholderImg; // Fallback if image not found
    }
  };

  return (
    <div className="user-card-container">
      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "open" : ""}`}>
        <button className="menu-btn inside-btn" onClick={toggleMenu}>←</button>
        <button onClick={() => navigate("/")}>Home</button>
        <button onClick={() => navigate("/about")}>About</button>
        <button onClick={() => navigate("/services")}>Services</button>
        <button onClick={() => navigate("/contact")}>Contact</button>
        <button onClick={() => navigate("/add-product")}>Add Product</button>
        <button className="delete-btn" onClick={deleteProduct}>❌ Delete Product</button>
      </div>

      {/* Main menu button outside */}
      {!isOpen && (
        <button className="menu-btn" onClick={toggleMenu}>☰ Menu</button>
      )}

      {/* Content Section */}
      <div className="content">
        <div className="container">
          {products.length > 0 ? (
            products.map((product) => (
              <div 
                key={product.id} 
                className="box" 
                onClick={() => navigate(`/product/${product.id}`)} 
              >
                <img 
                  src={getImagePath(product.img)} 
                  alt={product.name} 
                  className="product-img" 
                  onError={(e) => { e.target.src = placeholderImg; }} 
                />
                <p className="description">
                  <strong>{product.name}</strong>
                </p>
                <p>{product.description}</p>
              </div>
            ))
          ) : (
            <p className="no-products">No products available.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserCard;
