import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./UserCard.css";

const UserCard = ({ products, setProducts, logout }) => { // Add logout prop
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  // Toggle Sidebar
  const toggleMenu = () => setIsOpen(!isOpen);

  // Function to Delete the First Product
  const deleteProduct = () => {
    if (products.length === 0) {
      alert("No products to delete!");
      return;
    }

    const updatedProducts = products.slice(1);
    setProducts(updatedProducts);
    localStorage.setItem("products", JSON.stringify(updatedProducts)); // Update local storage
  };

  return (
    <div className="user-card-container">
      {/* Menu Button (Top-Left Corner) */}
      {!isOpen && (
        <button className="menu-btn" onClick={toggleMenu}>☰ Menu</button>
      )}

      {/* Logout Button (Top-Right Corner) */}
      <button className="logout-btn" onClick={logout}>Logout</button>

      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "open" : ""}`}>
        {/* Back Button (100% width, top of sidebar) */}
        <button className="back-btn" onClick={toggleMenu}>←</button>

        {/* Sidebar Links */}
        <button onClick={() => navigate("/")}>Home</button>
        <button onClick={() => navigate("/about")}>About</button>
        <button onClick={() => navigate("/services")}>Services</button>
        <button onClick={() => navigate("/contact")}>Contact</button>
        <button onClick={() => navigate("/add-product")}>Add Product</button>
        <button className="delete-btn" onClick={deleteProduct}>❌ Delete Product</button>
      </div>

      {/* Content Section */}
      <div className="content">
        <div className="container">
          {products.length > 0 ? (
            products.map((product, index) => (
              <div
                key={product.id || index} 
                className="box"
                onClick={() => navigate(`/product/${product.id || index}`)}
              >
                <img
                  src={product.img}
                  alt={product.name}
                  className="product-img"
                  onError={(e) => (e.target.src = "/placeholder.jpg")} // Fallback image
                />
                <p className="description">
                  <strong>{product.name}</strong>
                </p>
                <p>{product.description}</p>
              </div>
            ))
          ) : (
            <p className="no-products">No products available.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserCard;
