import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import UserCard from "./components/UserCard.jsx";
import VodkaPage from "./components/VodkaPage.jsx";
import BuyNowPage from "./components/buynowpage.jsx";
import AddProduct from "./components/AddProduct.jsx";  // Import the new AddProduct page

function App() {
  const [products, setProducts] = useState([
    { id: "vodka", name: "Magic Moments Grain Vodka", img: "/assets/alc.jpeg", description: "Smooth and refined vodka." },
    { id: "whiskey", name: "Magic Moments Grain Whiskey", img: "/assets/alc.jpeg", description: "Aged whiskey with a rich taste." },
  ]);

  // Function to add a new product dynamically
  const addNewProduct = (product) => {
    setProducts([...products, { 
      id: product.name.toLowerCase().replace(/\s+/g, "-"), 
      name: product.name, 
      img: product.img, 
      description: product.description 
    }]);
  };

  return (
    <Router>
      <div className="info">
        <Routes>
          <Route path="/" element={<UserCard products={products} />} />
          <Route path="/vodka" element={<VodkaPage />} />
          <Route path="/buy-now" element={<BuyNowPage />} />
          <Route path="/add-product" element={<AddProduct addNewProduct={addNewProduct} />} /> 
        </Routes>
      </div>
    </Router>
  );
}

export default App;
