import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import UserCard from "./components/UserCard.jsx";
import ProductPage from "./components/ProductPage.jsx";
import BuyNowPage from "./components/buynowpage.jsx";
import AddProduct from "./components/AddProduct.jsx";
import Login from "./components/Login.jsx";
import Signup from "./components/Signup.jsx";
import Navbar from "./components/Navbar.jsx";

function App() {
  // Load products from local storage
  const getStoredProducts = () => {
    try {
      const storedProducts = localStorage.getItem("products");
      return storedProducts ? JSON.parse(storedProducts) : [];
    } catch (error) {
      console.error("Error loading products:", error);
      return [];
    }
  };

  const [products, setProducts] = useState(() => getStoredProducts());
  const [user, setUser] = useState(localStorage.getItem("token") || null);

  useEffect(() => {
    localStorage.setItem("products", JSON.stringify(products));
  }, [products]);

  const addProduct = (newProduct) => {
    const updatedProducts = [...products, { ...newProduct, id: Date.now().toString() }];
    setProducts(updatedProducts);
  };

  const logout = () => {
    localStorage.removeItem("token");
    setUser(null);
  };

  return (
    <Router>
      <Navbar user={user} logout={logout} />
      <div className="container">
        <Routes>
          <Route path="/" element={<UserCard products={products} setProducts={setProducts} />} />
          <Route path="/product/:id" element={<ProductPage products={products} />} />
          <Route path="/buy-now" element={<BuyNowPage />} />
          <Route
            path="/add-product"
            element={user ? <AddProduct addProduct={addProduct} /> : <Navigate to="/login" />}
          />
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/signup" element={<Signup />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
