import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from "react-router-dom";
import UserCard from "./components/UserCard.jsx";
import ProductPage from "./components/ProductPage.jsx";
import BuyNowPage from "./components/buynowpage.jsx";
import AddProduct from "./components/AddProduct.jsx";
import Login from "./components/Login.jsx";
import Signup from "./components/Signup.jsx"; // Ensure the file name matches
import Navbar from "./components/Navbar.jsx";

// ✅ Private Route Wrapper (For Authenticated Users)
const PrivateRoute = ({ user }) => {
  return user ? <Outlet /> : <Navigate to="/login" replace />;
};

function App() {
  const [user, setUser] = useState(!!localStorage.getItem("token"));
  const [products, setProducts] = useState(() => {
    return JSON.parse(localStorage.getItem("products")) || [];
  });

  // ✅ Sync products with localStorage
  useEffect(() => {
    localStorage.setItem("products", JSON.stringify(products));
  }, [products]);

  // ✅ Logout Function
  const logout = () => {
    localStorage.removeItem("token");
    setUser(false);
  };

  // ✅ Add Product Function
  const addProduct = (newProduct) => {
    setProducts((prevProducts) => [
      ...prevProducts,
      { ...newProduct, id: Date.now().toString() }, // Unique ID using timestamp (as string)
    ]);
  };

  return (
    <Router>
      {/* Show Navbar only when user is logged in */}
      {user && <Navbar logout={logout} />}

      <div className="container">
        <Routes>
          {/* Redirect to Dashboard if Logged In, otherwise to Login */}
          <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} />} />

          {/* ✅ Private Routes for Authenticated Users */}
          <Route element={<PrivateRoute user={user} />}>
            <Route path="/dashboard" element={<UserCard products={products} setProducts={setProducts} logout={logout} />} />
            <Route path="/add-product" element={<AddProduct addProduct={addProduct} />} />
          </Route>

          {/* ✅ Public Routes */}
          <Route path="/product/:id" element={<ProductPage products={products} />} />
          <Route path="/buy-now" element={<BuyNowPage />} />
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/signup" element={<Signup />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
