import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AddProduct.css";

const AddProduct = ({ addProduct }) => {
  const [product, setProduct] = useState({ name: "", img: "", description: "", price: "" });
  const [preview, setPreview] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });

    // If it's an image URL, update preview
    if (name === "img") {
      setPreview(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!product.name || !product.price) {
      alert("Name and Price are required!");
      return;
    }
    addProduct(product);
    navigate("/dashboard");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" placeholder="Product Name" value={product.name} onChange={handleChange} required />
      <input type="text" name="img" placeholder="Image URL" value={product.img} onChange={handleChange} />
      
      {/* âœ… Image Preview */}
      {preview && (
        <div className="image-preview">
          <img src={preview} alt="Product Preview" onError={(e) => (e.target.src = "/placeholder.jpg")} />
        </div>
      )}
      
      <textarea name="description" placeholder="Description" value={product.description} onChange={handleChange} />
      <input type="number" name="price" placeholder="Price" value={product.price} onChange={handleChange} required />
      <button type="submit">Add Product</button>
    </form>
  );
};

export default AddProduct;
