import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from "react-router-dom";
import UserCard from "./components/UserCard.jsx";
import ProductPage from "./components/ProductPage.jsx";
import BuyNowPage from "./components/buynowpage.jsx";
import AddProduct from "./components/AddProduct.jsx";
import Login from "./components/Login.jsx";
import Signup from "./components/Sign.jsx";
import Navbar from "./components/Navbar.jsx";

// üîπ Private Route Component (Protects Pages)
const PrivateRoute = ({ user }) => {
  return user ? <Outlet /> : <Navigate to="/login" replace />;
};

function App() {
  const [user, setUser] = useState(!!localStorage.getItem("token"));
  const [products, setProducts] = useState(() => {
    return JSON.parse(localStorage.getItem("products")) || [];
  });

  // üîπ Persist products in local storage
  useEffect(() => {
    localStorage.setItem("products", JSON.stringify(products));
  }, [products]);

  // üîπ Logout function
  const logout = () => {
    localStorage.removeItem("token");
    setUser(false);
  };

  return (
    <Router>
      {/* ‚úÖ Show Navbar only when user is logged in */}
      {user && <Navbar user={user} logout={logout} />}

      <div className="container">
        <Routes>
          {/* üîπ Redirect Home to Dashboard if logged in, otherwise to Login */}
          <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} />} />

          {/* üîí Private Routes (Requires Login) */}
          <Route element={<PrivateRoute user={user} />}>
            <Route path="/dashboard" element={<UserCard products={products} setProducts={setProducts} />} />
            <Route path="/add-product" element={<AddProduct setProducts={setProducts} />} />
          </Route>

          {/* üåç Public Routes (No Authentication Required) */}
          <Route path="/product/:id" element={<ProductPage products={products} />} />
          <Route path="/buy-now" element={<BuyNowPage />} />
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/signup" element={<Signup />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
