import React, { useState } from "react";
import { useLocation } from "react-router-dom"; // ✅ Get product data from navigation
import "./buynowpage.css"; // ✅ Import CSS file

const BuyNowPage = () => {
  const location = useLocation();
  const product = location.state?.product; // ✅ Get product from navigation
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    address: "",
    phone: "",
  });

  const [loading, setLoading] = useState(false); // ✅ Prevent multiple submissions

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // ✅ Disable button while sending data

    try {
      if (!product) {
        alert("❌ No product selected!");
        return;
      }

      const response = await fetch("http://localhost:5000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(orderData),
      });
      

      const data = await response.json(); // ✅ Get response data

      if (response.ok) {
        alert("✅ Order placed successfully!");
        console.log("Order Success:", data);
        setFormData({ name: "", email: "", address: "", phone: "" });
      } else {
        alert(`❌ Error: ${data.error || "Failed to place order"}`);
        console.error("Order Error:", data);
      }
    } catch (error) {
      alert("❌ Network error! Check console for details.");
      console.error("Network Error:", error);
    } finally {
      setLoading(false); // ✅ Enable button after response
    }
  };

  return (
    <div className="buy-now-container">
      <h2>Buy Now</h2>

      {/* ✅ Show selected product details */}
      {product ? (
        <div className="product-preview">
          <img src={product.img || "/placeholder.jpg"} alt={product.name} className="product-img" />
          <h3>{product.name}</h3>
          <p>{product.description}</p>
          <p><strong>Price:</strong> ${product.price}</p>
        </div>
      ) : (
        <p className="error">❌ No product selected!</p>
      )}

      <form className="buy-now-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Your Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Your Address"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Your Phone Number"
          value={formData.phone}
          onChange={handleChange}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Order"}
        </button>
      </form>
    </div>
  );
};

export default BuyNowPage;
