import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom"; 
import alcImage from "../assets/alc.jpeg"; 
import whiskeyImage from "../assets/whiskey.jpeg"; 
import "./UserCard.css";

const UserCard = () => { 
  const [isOpen, setIsOpen] = useState(false);
  const contentRef = useRef(null);
  const navigate = useNavigate(); 

  // Product List (Initially Hardcoded)
  const [products, setProducts] = useState([
    { id: "vodka", name: "Magic Moments Grain Vodka", img: alcImage, description: "Smooth and refined vodka." },
    { id: "whiskey", name: "Magic Moments Grain Whiskey", img: whiskeyImage, description: "Aged whiskey with a rich taste." }, 
  ]);

  // Toggle Sidebar
  const toggleMenu = () => setIsOpen(!isOpen);

  // Function to Delete the First Product (Modify as Needed)
  const deleteProduct = () => {
    if (products.length === 0) {
      alert("No products to delete!");
      return;
    }
    
    const updatedProducts = products.slice(1); // Removes the first product
    setProducts(updatedProducts);
  };

  return (
    <div className="user-card-container">
      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "open" : ""}`}>
        <button className="menu-btn inside-btn" onClick={toggleMenu}>←</button>
        <button onClick={() => navigate("/")}>Home</button>
        <button onClick={() => navigate("/about")}>About</button>
        <button onClick={() => navigate("/services")}>Services</button>
        <button onClick={() => navigate("/contact")}>Contact</button>
        <button onClick={() => navigate("/add-product")}>Add Product</button>
        <button className="delete-btn" onClick={deleteProduct}>❌ Delete Product</button>
      </div>

      {/* Main menu button outside */}
      {!isOpen && (
        <button className="menu-btn" onClick={toggleMenu}>☰ Menu</button>
      )}

      {/* Content Section */}
      <div className="content" ref={contentRef}>
        <div className="container">
          {products.length > 0 ? (
            products.map((product) => (
              <div 
                key={product.id} 
                className="box" 
                onClick={() => navigate(`/product/${product.id}`)}
              >
                <img src={product.img} alt={product.name} className="product-img" />
                <p className="description">
                  <strong>{product.name}</strong>
                </p>
                <p>{product.description}</p>
              </div>
            ))
          ) : (
            <p className="no-products">No products available.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserCard;
