import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AddProduct.css";

export const AddProduct = ({ addNewProduct }) => {
  const [product, setProduct] = useState({ name: "", description: "", img: null });
  const navigate = useNavigate();

  // Handle Image Upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setProduct({ ...product, img: URL.createObjectURL(file) });
    }
  };

  // Submit New Product
  const handleSubmit = () => {
    if (!product.name || !product.description || !product.img) {
      alert("Please fill in all fields including an image.");
      return;
    }

    addNewProduct(product);
    navigate("/"); // Redirect back to homepage after adding
  };

  return (
    <div className="add-product-page">
      <h2>Add a New Product</h2>
      <input
        type="text"
        placeholder="Product Name"
        value={product.name}
        onChange={(e) => setProduct({ ...product, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Product Description"
        value={product.description}
        onChange={(e) => setProduct({ ...product, description: e.target.value })}
      />
      <input
        type="file"
        accept="image/*"
        onChange={handleImageUpload}
      />
      {product.img && <img src={product.img} alt="Preview" className="preview-img" />}
      <button onClick={handleSubmit}>Add Product</button>
    </div>
  );
};

export default AddProduct;
