import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom"; 
import alcImage from "../assets/alc.jpeg"; 
import "./UserCard.css";

const UserCard = () => {
  const [isOpen, setIsOpen] = useState(false);
  const contentRef = useRef(null);
  const navigate = useNavigate(); 

  // Product List (Initially Hardcoded)
  const [products, setProducts] = useState([
    { id: "vodka", name: "Magic Moments Grain Vodka", img: alcImage, description: "Smooth and refined vodka." },
    { id: "whiskey", name: "Magic Moments Grain Whiskey", img: alcImage, description: "Aged whiskey with a rich taste." },
  ]);

  // New Product Form
  const [newProduct, setNewProduct] = useState({ name: "", description: "", img: null });

  const toggleMenu = () => setIsOpen(!isOpen);

  // Handle Image Upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setNewProduct({ ...newProduct, img: URL.createObjectURL(file) });
    }
  };

  // Add New Product
  const addProduct = () => {
    if (!newProduct.name.trim() || !newProduct.description.trim() || !newProduct.img) {
      alert("Please fill in all fields including an image.");
      return;
    }

    setProducts([...products, { 
      id: newProduct.name.toLowerCase().replace(/\s+/g, "-"), 
      name: newProduct.name, 
      img: newProduct.img, 
      description: newProduct.description 
    }]);

    setNewProduct({ name: "", description: "", img: null }); // Reset input fields
  };

  return (
    <div className="user-card-container">
      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "open" : ""}`}>
        <button className="menu-btn inside-btn" onClick={toggleMenu}>←</button>
        <button onClick={() => navigate("/")}>Home</button>
        <button onClick={() => navigate("/about")}>About</button>
        <button onClick={() => navigate("/services")}>Services</button>
        <button onClick={() => navigate("/contact")}>Contact</button>
        <button onClick={() => navigate("/add-product")}>Add Product</button>
      </div>

      {/* Main menu button outside */}
      {!isOpen && (
        <button className="menu-btn" onClick={toggleMenu}>☰ Menu</button>
      )}

      {/* Content Section */}
      <div className="content" ref={contentRef}>
        <div className="container">
          {products.map((product) => (
            <div key={product.id} className="box" onClick={() => navigate(`/product/${product.id}`)}>
              <img src={product.img} alt={product.name} className="product-img" />
              <p className="description">
                <strong>{product.name}</strong>
              </p>
              <p>{product.description}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Add New Product */}
      
    </div>
  );
};

export default UserCard;
