import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import UserCard from "./components/UserCard.jsx";
import ProductPage from "./components/ProductPage.jsx";
import BuyNowPage from "./components/BuyNowPage.jsx";
import AddProduct from "./components/AddProduct.jsx";
import Login from "./components/Login.jsx";
import Signup from "./components/Signup.jsx";
import Navbar from "./components/Navbar.jsx";

function App() {
  // Check if user is logged in (from localStorage)
  const [user, setUser] = useState(localStorage.getItem("token") || null);
  const [products, setProducts] = useState(() => {
    const storedProducts = localStorage.getItem("products");
    return storedProducts ? JSON.parse(storedProducts) : [];
  });

  useEffect(() => {
    localStorage.setItem("products", JSON.stringify(products));
  }, [products]);

  // Logout Function
  const logout = () => {
    localStorage.removeItem("token");
    setUser(null);
  };

  return (
    <Router>
      <Navbar user={user} logout={logout} />
      <div className="container">
        <Routes>
          {/* Redirect Home '/' to Login */}
          <Route path="/" element={user ? <Navigate to="/dashboard" /> : <Navigate to="/login" />} />

          {/* Protected Route: UserCard (Dashboard) */}
          <Route
            path="/dashboard"
            element={user ? <UserCard products={products} setProducts={setProducts} /> : <Navigate to="/login" />}
          />

          {/* Product Details & Buy Now */}
          <Route path="/product/:id" element={<ProductPage products={products} />} />
          <Route path="/buy-now" element={<BuyNowPage />} />

          {/* Protected Route: Add Product */}
          <Route path="/add-product" element={user ? <AddProduct /> : <Navigate to="/login" />} />

          {/* Authentication Routes */}
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/signup" element={<Signup />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
