import React from "react";
import alcImage from "../assets/alc.jpeg"; 
import { useNavigate } from "react-router-dom"; 
import "./VodkaPage.css";

export const VodkaPage = () => {
  const navigate = useNavigate(); // ✅ Define navigate

  return (
    <div>
      <div className="imgclass">
        <img src={alcImage} alt="Magic Moments Grain Vodka" />
        <h2>
          Vodka's alcohol content can range from 37.5% to 95% alcohol by volume (ABV). 
          The standard vodka in the United States is 40% ABV (80 proof).
        </h2>
        <span className="dis">-33%</span>
        <span className="rupee-sy">₹</span>
        <span className="rupee-price">500</span>
        <hr width="100%" size="2" color="gray"></hr>
        <button className="addcart">Add to cart</button>
        <button className="buynow" onClick={() => navigate("/buy-now")}>
          Buy Now
        </button>
      </div>
    </div>
  );
};
