import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const ProductPage = ({ products }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    let storedProducts = [];

    // Check if `products` is valid, else load from `localStorage`
    if (Array.isArray(products) && products.length > 0) {
      storedProducts = products;
    } else {
      const localStorageProducts = localStorage.getItem("products");
      if (localStorageProducts) {
        storedProducts = JSON.parse(localStorageProducts);
      }
    }

    // Find the product by ID (convert ID to number for comparison)
    const foundProduct = storedProducts.find((p) => Number(p.id) === Number(id));

    setProduct(foundProduct || null);
  }, [id, products]);

  if (!product) {
    return <h2 style={{ textAlign: "center", color: "red" }}>Product Not Found</h2>;
  }

  return (
    <div className="product-page-container">
      <h2>{product.name}</h2>
      <img 
        src={`/assets/${product.img}`} 
        alt={product.name} 
        className="product-img" 
        onError={(e) => (e.target.src = "/assets/placeholder.jpg")} 
      />
      <p>{product.description}</p>
      <button onClick={() => navigate("/")}>â¬… Back to Home</button>
    </div>
  );
};

export default ProductPage;
