import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import UserCard from "./components/UserCard.jsx";
import ProductPage from "./components/ProductPage.jsx"; 
import BuyNowPage from "./components/buynowpage.jsx";
import AddProduct from "./components/AddProduct.jsx";

function App() {
  const [products, setProducts] = useState([
    { id: "vodka", name: "Magic Moments Grain Vodka", img: "/vodka.jpg", description: "Smooth and refined vodka." },
    { id: "whiskey", name: "Magic Moments Grain Whiskey", img: "/whiskey.jpg", description: "Aged whiskey with a rich taste." },
  ]);

  const addProduct = (newProduct) => {
    setProducts([...products, { ...newProduct, id: Date.now().toString() }]);
  };

  return (
    <Router>
      <div className="info">
        <Routes>
          <Route path="/" element={<UserCard products={products} />} />
          <Route path="/product/:id" element={<ProductPage />} /> 
          <Route path="/buy-now" element={<BuyNowPage />} />
          <Route path="/add-product" element={<AddProduct addProduct={addProduct} />} /> 
          {/* âœ… addProduct is now passed correctly */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
