import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./ProductPage.css";

const ProductPage = ({ products = [] }) => { // ✅ Default to an empty array
  const { id } = useParams();
  const navigate = useNavigate();

  // ✅ Handle the case where products is not an array
  if (!Array.isArray(products)) {
    return <h2 style={{ textAlign: "center", color: "red" }}>Error loading products!</h2>;
  }

  // ✅ Find the product by ID safely
  const product = products.find((p) => p.id.toString() === id);

  // ✅ Show error message if product is not found
  if (!product) {
    return <h2 style={{ textAlign: "center", color: "red" }}>Product not found!</h2>;
  }

  return (
    <div className="product-details">
      <h2>{product.name}</h2>
      <img src={product.img} alt={product.name} className="product-image" onError={(e) => e.target.src = "/assets/placeholder.jpg"} />
      <p>{product.description}</p>
      <button onClick={() => navigate("/")}>⬅ Go Back</button>
    </div>
  );
};

export default ProductPage;
