import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const AddProduct = ({ addProduct }) => { 
  // ✅ Hooks must always be at the top level of the component
  const navigate = useNavigate();
  const [product, setProduct] = useState({ name: "", description: "", img: null });

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setProduct({ ...product, img: URL.createObjectURL(file) });
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!product.name.trim() || !product.description.trim() || !product.img) {
      alert("Please fill in all fields.");
      return;
    }

    if (typeof addProduct === "function") { 
      addProduct(product); // ✅ Ensure addProduct is a function before calling
      navigate("/"); // Redirect to home
    } else {
      console.error("❌ addProduct is not a function. Make sure it's passed correctly.");
    }
  };

  return (
    <div className="add-product-container">
      <h2>Add New Product</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Product Name"
          value={product.name}
          onChange={(e) => setProduct({ ...product, name: e.target.value })}
        />
        <textarea
          placeholder="Product Description"
          value={product.description}
          onChange={(e) => setProduct({ ...product, description: e.target.value })}
        />
        <input type="file" onChange={handleImageUpload} />
        {product.img && <img src={product.img} alt="Preview" className="preview-img" />}
        <button type="submit">Add Product</button>
      </form>
    </div>
  );
};

export default AddProduct;
