import React from "react";
import { useParams, useNavigate } from "react-router-dom";

const ProductPage = ({ products }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  const product = products.find((p, index) => String(p.id || index) === id);

  if (!product) return <h2>❌ Product not found</h2>;

  // ✅ Navigate to BuyNowPage with product details
  const handleBuyNow = () => {
    navigate("/buynowpage", { state: { product } }); // Pass product details
  };

  return (
    <div className="product-page">
      <h2>{product.name}</h2>
      <img src={product.img || "/placeholder.jpg"} alt={product.name} className="product-image" />
      <p>{product.description}</p>
      <p><strong>Price:</strong> ${product.price}</p>

      {/* ✅ Buy Now Button */}
      <button className="buy-now-btn" onClick={handleBuyNow}>Buy Now</button>
    </div>
  );
};

export default ProductPage;
