import React, { useState } from "react";
import "./buynowpage.css"; // ✅ Import CSS file

const BuyNowPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    address: "",
    phone: "",
  });

  const [loading, setLoading] = useState(false); // ✅ Prevent multiple submissions

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // ✅ Disable button while sending data

    try {
      console.log("Sending Order:", formData); // ✅ Debugging log

      const response = await fetch("http://localhost:5000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json(); // ✅ Get response data

      if (response.ok) {
        alert("✅ Order placed successfully!");
        console.log("Order Success:", data);
        setFormData({ name: "", email: "", address: "", phone: "" });
      } else {
        alert(`❌ Error: ${data.error || "Failed to place order"}`);
        console.error("Order Error:", data);
      }
    } catch (error) {
      alert("❌ Network error! Check console for details.");
      console.error("Network Error:", error);
    } finally {
      setLoading(false); // ✅ Enable button after response
    }
  };

  return (
    <div className="buy-now-container">
      <h2>Buy Now</h2>
      <form className="buy-now-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Your Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Your Address"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Your Phone Number"
          value={formData.phone}
          onChange={handleChange}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Order"}
        </button>
      </form>
    </div>
  );
};

export default BuyNowPage;
