import React from "react";
import { useParams, useNavigate } from "react-router-dom";

const ProductPage = ({ products = [] }) => {  // ✅ Default value to prevent undefined
  const { id } = useParams();
  const navigate = useNavigate();

  if (!Array.isArray(products)) {
    console.error("ProductPage received an invalid products prop:", products);
    return <h2>Error: Invalid product data!</h2>;
  }

  // Find product by ID (Ensure ID is a number)
  const product = products.find((p) => p.id === id || p.id === Number(id));

  if (!product) {
    return <h2 style={{ textAlign: "center", color: "red" }}>Product Not Found!</h2>;
  }

  return (
    <div className="product-details">
      <h2>{product.name}</h2>
      <img src={product.img} alt={product.name} className="product-image" />
      <p>{product.description}</p>
      <button onClick={() => navigate("/")}>⬅ Go Back</button>
    </div>
  );
};

export default ProductPage;
