import React from "react";
import { useParams, useNavigate } from "react-router-dom";

const ProductPage = ({ products = [] }) => {  // ✅ Default value to prevent undefined
  const { id } = useParams();
  const navigate = useNavigate();

  if (!products || products.length === 0) {  // ✅ Handle undefined or empty products
    return <h2 style={{ textAlign: "center", color: "red" }}>Loading products...</h2>;
  }

  // Ensure ID is a number for comparison
  const product = products.find((p) => p.id === Number(id));

  if (!product) {
    return <h2 style={{ textAlign: "center", color: "red" }}>Product not found!</h2>;
  }

  return (
    <div className="product-page-container">
      <h2>{product.name}</h2>
      <img 
        src={product.img} 
        alt={product.name} 
        className="product-img" 
        onError={(e) => e.target.src = "/placeholder.jpg"} 
      />
      <p>{product.description}</p>
      <button onClick={() => navigate("/")}>Back to Home</button>
    </div>
  );
};

export default ProductPage;
