import React, { useState } from "react";
import nitin from "../assets/nitin.jpg";
import "./UserCard.css";

export const UserCard = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div>
      <div className="info">
        {/* Button to toggle menu */}
        <button className="menu-btn" onClick={toggleMenu}>â˜° Menu</button>

        {/* Sidebar menu */}
        <div className={`sidebar ${isOpen ? "open" : ""}`}>
          <a href="#">Home</a>
          <a href="#">About</a>
          <a href="#">Services</a>
          <a href="#">Contact</a>
        </div>
      </div>
    </div>
  );
};
