import React, { useState } from "react";
import nitin from "../assets/nitin.jpg";
import "./UserCard.css";

export const UserCard = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div>
      {}
      <div className={`sidebar ${isOpen ? "open" : ""}`}>
        {}
        <button className="menu-btn inside-btn" onClick={toggleMenu}>
          ← 
        </button>
        <a href="#">Home</a>
        <a href="#">About</a>
        <a href="#">Services</a>
        <a href="#">Contact</a>
      </div>

      {}
      {!isOpen && (
        <button className="menu-btn" onClick={toggleMenu}>
          ☰ Menu
        </button>
      )}
    </div>
  );
};
