import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import alcImage from "../assets/alc.jpeg"; // Vodka Image
import whiskeyImage from "../assets/whiskey.jpeg"; // Whiskey Image
import "./ProductPage.css";

// Product Data with Separate Images
const products = {
  vodka: { name: "Magic Moments Grain Vodka", img: alcImage, price: "500", discount: "-33%" },
  whiskey: { name: "Magic Moments Grain Whiskey", img: whiskeyImage, price: "700", discount: "-20%" },
};

export const ProductPage = () => {
  const { id } = useParams(); // Get product ID from URL
  const navigate = useNavigate();
  const product = products[id]; // Fetch product details

  if (!product) {
    return <h2>Product Not Found</h2>;
  }

  return (
    <div>
      <div className="imgclass">
        <img src={product.img} alt={product.name} />
        <h2>{product.name}</h2>
        <span className="dis">{product.discount}</span>
        <span className="rupee-sy">â‚¹</span>
        <span className="rupee-price">{product.price}</span>
        <hr width="100%" size="2" color="gray"></hr>
        <button className="addcart">Add to Cart</button>
        <button className="buynow" onClick={() => navigate("/buy-now")}>
          Buy Now
        </button>
      </div>
    </div>
  );
};

export default ProductPage;
