import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from "react-router-dom";
import UserCard from "./components/UserCard.jsx";
import ProductPage from "./components/ProductPage.jsx";
import BuyNowPage from "./components/buynowpage.jsx";
import AddProduct from "./components/AddProduct.jsx";
import Login from "./components/Login.jsx";
import Signup from "./components/Sign.jsx"; // âœ… Fixed Import
import Navbar from "./components/Navbar.jsx";

// ðŸ”¹ Private Route Component
const PrivateRoute = ({ user }) => {
  return user ? <Outlet /> : <Navigate to="/login" />;
};

function App() {
  const [user, setUser] = useState(localStorage.getItem("token") ? true : false);
  const [products, setProducts] = useState(() => {
    const storedProducts = localStorage.getItem("products");
    return storedProducts ? JSON.parse(storedProducts) : [];
  });

  useEffect(() => {
    localStorage.setItem("products", JSON.stringify(products));
  }, [products]);

  const logout = () => {
    localStorage.removeItem("token");
    setUser(false);
  };

  return (
    <Router>
      {/* âœ… Show Navbar only if user is logged in */}
      {user && <Navbar user={user} logout={logout} />}

      <div className="container">
        <Routes>
          {/* Redirect Home '/' to Dashboard or Login */}
          <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} />} />

          {/* ðŸ”¹ Private Routes (Requires Login) */}
          <Route path="/" element={<PrivateRoute user={user} />}>
            <Route path="/dashboard" element={<UserCard products={products} setProducts={setProducts} />} />
            <Route path="/add-product" element={<AddProduct />} />
          </Route>

          {/* ðŸ”¹ Public Routes (No Authentication Required) */}
          <Route path="/product/:id" element={<ProductPage products={products} />} />
          <Route path="/buy-now" element={<BuyNowPage />} />
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/signup" element={<Signup />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
