import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom"; 
import alcImage from "../assets/alc.jpeg"; 
import "./UserCard.css";

export const UserCard = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const contentRef = useRef(null);
  const navigate = useNavigate(); 

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleSearch = () => {
    if (contentRef.current) {
      const text = contentRef.current.innerText.toLowerCase();
      const query = searchQuery.toLowerCase();

      if (query && text.includes(query)) {
        const index = text.indexOf(query);
        if (index !== -1) {
          const range = document.createRange();
          const selection = window.getSelection();
          selection.removeAllRanges();

          let foundNode = null;
          contentRef.current.childNodes.forEach((node) => {
            if (node.nodeType === 3 && node.textContent.toLowerCase().includes(query)) {
              foundNode = node;
            }
          });

          if (foundNode) {
            range.selectNodeContents(foundNode);
            selection.addRange(range);
            foundNode.parentElement.scrollIntoView({ behavior: "smooth", block: "center" });
          }
        }
      }
    }
  };

  return (
    <div>
      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          className="search-bar"
          placeholder="Search..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button className="search-btn" onClick={handleSearch}>üîç</button>
      </div>

      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "open" : ""}`}>
        <button className="menu-btn inside-btn" onClick={toggleMenu}>
          ‚Üê
        </button>
        <button onClick={() => navigate("/")}>Home</button>
        <button onClick={() => navigate("/about")}>About</button>
        <button onClick={() => navigate("/services")}>Services</button>
        <button onClick={() => navigate("/contact")}>Contact</button>
      </div>

      {/* Main menu button outside */}
      {!isOpen && (
        <button className="menu-btn" onClick={toggleMenu}>
          ‚ò∞ Menu
        </button>
      )}

      {/* Content Section */}
      <div className="content" ref={contentRef}>
        <div className="container">
          <div className="box" onClick={() => navigate("/vodka")}>
            <img src={alcImage} alt="Magic Moments Grain Vodka" />
            <p className="description">
              <strong>Magic Moments Grain Vodka</strong>
            </p>
          </div>
          <div className="box" onClick={() => navigate("/vodka")}>
            <img src={alcImage} alt="Magic Moments Grain Vodka" />
            <p className="description">
              <strong>Magic Moments Grain Vodka</strong>
            </p>
          </div>
          <div className="box">Box 3</div>
          <div className="box">Box 4</div>
          <div className="box">Box 5</div>
        </div>
      </div>
    </div>
  );
};

export default UserCard;
