import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom"; 
import alcImage from "../assets/alc.jpeg"; 
import "./UserCard.css";

export const UserCard = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const contentRef = useRef(null);
  const navigate = useNavigate(); 

  // Product List (Initially Hardcoded)
  const [products, setProducts] = useState([
    { id: "vodka", name: "Magic Moments Grain Vodka", img: alcImage, description: "Smooth and refined vodka." },
    { id: "whiskey", name: "Magic Moments Grain Whiskey", img: alcImage, description: "Aged whiskey with a rich taste." },
  ]);

  // New Product Form
  const [newProduct, setNewProduct] = useState({ name: "", description: "", img: null });

  const toggleMenu = () => setIsOpen(!isOpen);

  // Search Functionality
  const handleSearch = () => {
    if (contentRef.current) {
      const text = contentRef.current.innerText.toLowerCase();
      const query = searchQuery.toLowerCase();

      if (query && text.includes(query)) {
        const index = text.indexOf(query);
        if (index !== -1) {
          const range = document.createRange();
          const selection = window.getSelection();
          selection.removeAllRanges();

          let foundNode = null;
          contentRef.current.childNodes.forEach((node) => {
            if (node.nodeType === 3 && node.textContent.toLowerCase().includes(query)) {
              foundNode = node;
            }
          });

          if (foundNode) {
            range.selectNodeContents(foundNode);
            selection.addRange(range);
            foundNode.parentElement.scrollIntoView({ behavior: "smooth", block: "center" });
          }
        }
      }
    }
  };

  // Handle Image Upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setNewProduct({ ...newProduct, img: URL.createObjectURL(file) });
    }
  };

  // Add New Product
  const addProduct = () => {
    if (!newProduct.name || !newProduct.description || !newProduct.img) {
      alert("Please fill in all fields including an image.");
      return;
    }

    setProducts([...products, { 
      id: newProduct.name.toLowerCase().replace(/\s+/g, "-"), 
      name: newProduct.name, 
      img: newProduct.img, 
      description: newProduct.description 
    }]);

    setNewProduct({ name: "", description: "", img: null }); // Reset input fields
  };

  return (
    <div>
      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          className="search-bar"
          placeholder="Search..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button className="search-btn" onClick={handleSearch}>üîç</button>
      </div>

      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "open" : ""}`}>
        <button className="menu-btn inside-btn" onClick={toggleMenu}>‚Üê</button>
        <button onClick={() => navigate("/")}>Home</button>
        <button onClick={() => navigate("/about")}>About</button>
        <button onClick={() => navigate("/services")}>Services</button>
        <button onClick={() => navigate("/contact")}>Contact</button>
      </div>

      {/* Main menu button outside */}
      {!isOpen && (
        <button className="menu-btn" onClick={toggleMenu}>‚ò∞ Menu</button>
      )}

      {/* Content Section */}
      <div className="content" ref={contentRef}>
        <div className="container">
          {products.map((product) => (
            <div key={product.id} className="box" onClick={() => navigate(`/product/${product.id}`)}>
              <img src={product.img} alt={product.name} />
              <p className="description">
                <strong>{product.name}</strong>
              </p>
            </div>
          ))}
        </div>
      </div>

      {/* Add New Product */}
      <div className="add-product-container">
        <input
          type="text"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Product Description"
          value={newProduct.description}
          onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
        />
        {newProduct.img && <img src={newProduct.img} alt="Preview" className="preview-img" />}
        <button onClick={addProduct}>Add Product</button>
      </div>
    </div>
  );
};

export default UserCard;
